  getTorrents(query: string, year?: string | number): Observable<any> {
    let result
    const REGEX_IMDB_ID = new RegExp(STRING_REGEX_IMDB_ID, `gi`);
    if (typeof query === 'string' && query.trim().match(REGEX_IMDB_ID)) {
