    this.sendToMain(collectionName, { operation: IpcOperations.GET_BY_PAGE, uuid: theUuid },
      { sort: sort, limit: limit, lastVal: lastVal });
    return this.listenOnce(`${collectionName}-${theUuid}`);
  }

