   */
  getMovieFromLibrary(arg): Promise<IRawLibrary> {
    const theUuid = uuidv4()
    this.sendToMain('library', { operation: IpcOperations.FIND, uuid: theUuid },
      { tmdbId: arg });
    return this.listenOnce(`library-${theUuid}`);
  }

