    @Inject('asyncValidator') asyncValidator?: AsyncValidatorFn | AsyncValidatorFn[] | null
  ) { super(formState, validatorOrOpts, asyncValidator) }

  ngOnInit() {
  }

  onChangeFunc(value) {

    // const value = event
    if (value) {
      console.log(value)
      this.onChange.emit(value)
    }
    // const value = event.source.value
