    age: 0,
    gender: '',
    authType: '',
    password: '',
    repeatPassword: ''
  }
  userSignIn = {
    usernameEmail: '',
    password: ''
  }
  signUpForm: FormGroup
  signInForm: FormGroup
  isSignIn = false
  isOAuthValid = false
