interface Headers {
  operation: IpcOperations,
  uuid: string
}

interface Body {
  tmdbId?: number
  idList?: number[]
  [x: string]: any
}

enum IpcOperations {
  FIND = 'find',
  FIND_ONE = 'find-one',
  FIND_IN_LIST = 'find-in-list',
  UPDATE = 'update',
  SAVE = 'save',
  REMOVE = 'remove',
  GET_BY_PAGE = 'get-by-page',
  COUNT = 'count'
}
export interface IBookmarkChanges {
  change: BookmarkChanges
}

export enum BookmarkChanges {
  UPDATE = 'update',
  DELETE = 'delete',
  INSERT = 'insert'
}

export interface IBookmark {
  tmdbId: number,
  imdbId: string,
  id: string
}



interface ILibrary {
  title?: string,
  year?: number,
  tmdbId?: number,
  imdbId?: string,
  libraryList: ILibraryData[]
}

interface ILibraryData {
  fullFilePath: string,
  _id: string
}

export interface IUserDataPaginated {
  totalPages: number,
  totalResults: number,
  page?: number,
  results: any[],
}

interface SortObject {
  [x: string]: 1 | -1
}

