  ) { this.db = this.angularFirestore.firestore }

  onSync() {

  }

  /**
   * Syncs bookmarks to and from cloud then executing batch commit.
   */
  synchronizeBookmarks() {
    // this.ipcService.call(this.ipcService.IPCCommand.GetBookmarkChanges)
    this.batch = this.db.batch()
    this.ipcService.bookmarkChanges.subscribe(e => {
      this.bookmarkInsertList = e.filter((v) => v.change === BookmarkChanges.INSERT)
      this.bookmarkDeleteList = e.filter((v) => v.change === BookmarkChanges.DELETE)
      this.bookmarkUpdateList = e.filter((v) => v.change === BookmarkChanges.UPDATE)
      this.batch = this.db.batch()
      this.insertItemsToFirestore()
      this.deleteItemsFromFirestore()
      this.batch.commit()
    })
  }

  getMovieUserData(id: number) {
    const bookmarkQuery = this.getFromFirestore(CollectionName.Bookmark, FieldName.TmdbId, FirebaseOperator.Equal, id)
    const watchedQuery = this.getFromFirestore(CollectionName.Watched, FieldName.TmdbId, FirebaseOperator.Equal, id)
    return new Promise<any>((resolve, reject) => {
      combineLatest([bookmarkQuery, watchedQuery]).toPromise().then(([val, val2]) => { resolve([val, val2]) })
    })
  }

  /**
   * Gets item from firestore.
   * @param collection name of collection
   * @param columnName name of column
   * @param operator firebase operator
   * @param value value to compare
   */
  getFromFirestore(collectionName: CollectionName, fieldName: FieldName, operator: FirebaseOperator, value: any) {
    return new Promise(resolve => {
      this.db.collection(collectionName).where(fieldName, operator, value).get().then((snapshot) => {
        console.log('SNAPSHOT: ', snapshot);
        if (!snapshot.empty) {
          const objectToReturn = snapshot.docs[0].data()
          objectToReturn['id'] = snapshot.docs[0].id
          resolve(objectToReturn)
        } else {
          resolve(null)
        }
      }).catch(err => {
        console.log('Error getting document', err);
      });
    })
  }

  getUserDataMultiple(idList: any[]): Promise<any> {
    const bookmarkQuery = this.getFromFirestoreMultiple(CollectionName.Bookmark, FieldName.TmdbId, idList)
    const watchedQuery = this.getFromFirestoreMultiple(CollectionName.Watched, FieldName.TmdbId, idList)
    return new Promise<any>((resolve, reject) => {
      combineLatest([bookmarkQuery, watchedQuery]).toPromise().then((val) => { resolve({ isFirebaseData: true, data: val }) })
    })
  }

  getFromFirestoreMultiple(collectionName: CollectionName, fieldName: FieldName, list: any[]): Promise<Array<QueryDocumentSnapshot<any>>> {
    return new Promise((resolve, reject) => {
      this.db.collection(collectionName).where(fieldName, FirebaseOperator.In, list).get().then((snapshot: firebase.firestore.QuerySnapshot) => {
        resolve(snapshot.docs)
      }).catch(err => {
        reject(err)
      })
    })
  }

  /**
   * IN PROGRESS.
   * @param collectionName name of the collection
   * @param order order
   * @param limit the limit
   * @param lastVal last value of previous page
   */
  getFromFirestoreMultiplePaginated(collectionName: CollectionName, order: string, limit?: number, lastVal?: string | number) {
    const defaultLimit = 20

    return new Promise(resolve => {
      lastVal = lastVal ? lastVal : 0
      // this.db.collection(collectionName).startAfter(lastDocId).orderBy(order, 'asc').limit(defaultLimit).get().then(snapshot => {
      this.db.collection(collectionName).orderBy(order).startAfter(lastVal).limit(defaultLimit).get().then(snapshot => {
        resolve(snapshot.docs)
      })
    })
  }

  /**
   * IN PROGRESS.
   * @param collectionName name of the collection
   * @param order order
   * @param limit the limit
   */
  getFromFirestoreMultiplePaginatedFirst(collectionName: CollectionName, order: string, limit?: number) {
    const defaultLimit = 20
    return new Promise(resolve => {
      this.db.collection(collectionName).orderBy(order, 'asc').limit(defaultLimit).get().then(snapshot => {
        resolve(snapshot.docs)
      })
    })

  }

