  insertIntoFirestore(collection: CollectionName, data: object) {
    return new Promise(resolve => {
      this.db.collection(collection).add(data).then(e => {
        if (e.id) {
          resolve(e.id)
        } else {
          resolve(null)
        }
      })
    })
  }

