   */
  sanitize(torrent: MDBTorrent) {
    let val
    if (torrent.hash.length !== 40) {
      val = this.getMagnetLinkWithImproperHash(torrent.hash, torrent.name);
    } else {
      val = this.getMagnetLinkWithProperHash(torrent.hash);
    }
