      root.displayMessage = ''
      root.displaySnackbar = false
    }, 3000);
  }

  /**
   * !UNUSED
   * Gets browser environment
   * @returns toReturn desktop or web
   */
  getEnvironment(): string {
    let toReturn = 'desktop'
    const environment = location.protocol
    if (environment === 'http:' || environment === 'https:') {
