  searchForm: FormGroup
  DEFAULT_SEARCH_QUERY: ISearchQuery = {
    query: '',
    yearFrom: 1901,
    isAvailable: '',
    yearTo: (new Date()).getFullYear(),
    genres: [],
    type: '',
