import { Component, OnInit, AfterViewInit, OnDestroy, Pipe, PipeTransform, ChangeDetectionStrategy, ChangeDetectorRef } from '@angular/core';
import { Router, ActivatedRoute } from '@angular/router';
import { DataService } from '@services/data.service';
import { GenreCodes } from '@models/interfaces';
import { DomSanitizer } from '@angular/platform-browser';
import { MovieService } from '@services/movie/movie.service';
import { UserDataService } from '@services/user-data/user-data.service';
import { WatchedService } from '@services/watched.service';
import { MDBMovie } from '@models/mdb-movie.model';
import GeneralUtil from '@utils/general.util';
import { TMDB_FULL_MOVIE_DETAILS } from 'app/mock-data-movie-details';

@Component({
  selector: 'app-preview',
  templateUrl: './preview.component.html',
  styleUrls: ['./preview.component.scss'],
  changeDetection: ChangeDetectionStrategy.OnPush
})
export class PreviewComponent implements OnInit, OnDestroy, AfterViewInit {

  constructor(
    private dataService: DataService,
    private watchedService: WatchedService,
    private router: Router,
    private activatedRoute: ActivatedRoute,
    private userDataService: UserDataService,
    private cdr: ChangeDetectorRef,
    private movieService: MovieService,
    private domSanitizer: DomSanitizer,
  ) { }

  previewMovie: MDBMovie
  clipSrc = null
  youtubeUrl = ''
  player;
  globalPlayerApiScript
  hasAlreadySelected: boolean;
  isYTReady = false
  hasInitialSelected = false
  isHide = true
  playedTmdbId = 0
  isMute = false
  isYTPlaying = false
  hasTrailerClip = false
  isAvailable = false
  procBookmark = false
  procWatched = false
  procHighlight = false
  showPreviewOverlayContext = false
  isTrailerOnly = false;

  ngOnInit() {
    this.frameReady()
  }

  ngAfterViewInit(): void {

    // this.dataService.previewMovie.subscribe((e: MDBMovie) => {
    //   console.log('PREVIEWMOVIE:', e)
      this.getVideoClip(new MDBMovie(TMDB_FULL_MOVIE_DETAILS))
      this.showPreviewOverlayContext = this.router.url.includes('/details/') ? false : true
      this.cdr.detectChanges()
    // })
  }

  ngOnDestroy() {
    this.removeYoutube()
  }

  frameReady() {
    (window as any).onYouTubeIframeAPIReady = () => {
      this.player = new (window as any).YT.Player('player', {
        height: '100%',
        width: '100%',
        events: {
          onReady: (event: any) => this.onPlayerReady(event),
          onStateChange: (event: any) => this.onPlayerStateChange(event)
        },
        playerVars: {
          autoplay: 0,
          controls: 0,
          modestbranding: 1,
          rel: 1,
          showInfo: 0,
          disablekb: 1
        }
      });
    }
  }

  onPlayerReady(event) {
    event.target.cueVideoById({
      videoId: this.youtubeUrl
    });
    event.target.playVideo();
  }

  /**
   * Detects the state change of trailer player (YouTube)
   * @param event
   */
  onPlayerStateChange(event) {
    /**
     * -1 (unstarted)
     * 0 (ended)
     * 1 (playing)
     * 2 (paused)
     * 3 (buffering)
     * 5 (video cued).
     * YT.PlayerState.ENDED
     * YT.PlayerState.PLAYING
     * YT.PlayerState.PAUSED
     * YT.PlayerState.BUFFERING
     * YT.PlayerState.CUED
     */
    console.log('onPlayerStateChange: ', event.data);
    if (event.data === 1) {
      this.isYTReady = true
      this.isYTPlaying = true
    }
    if (event.data === -1 || event.data === 5 || event.data === 0) {
      this.isYTReady = false
      this.isYTPlaying = false
    }
    if (event.data === 2) {
      const root = this
      console.log('paused')
      // setTimeout(() => {
      //   root.player.playVideo()
      //   console.log('settoplay')
      // }, 3000);
    }
    this.cdr.detectChanges()

  }

  /**
   * TODO: Simplify component code. Transfer codes to the service.
   * Performs actions for selected movie.
   * @param movie the selected movie
   */
  async getVideoClip(movie: MDBMovie) {
    this.previewMovie = movie
    this.isHide = false
    if (movie.tmdbId === this.playedTmdbId) {
      return
    }
